CorrsAndSysts::CorrsAndSysts(TString name, bool draw, bool HZZ)
    : m_debug(0),
      m_PMGSherpa22VJetsWeightTool(nullptr),
      m_zero(false),
      m_one(false),
      m_two(false),
      m_seven(false),
      m_eight(false),
      m_thirteen(false) {
  m_draw = draw;
  m_HZZ = HZZ;

  // set analysis basesd on contentes of name
  if (name.Contains("Zero") || name.Contains("zero") || name.Contains("ZERO")) {
    m_zero = true;
  }
  if (name.Contains("One") || name.Contains("one") || name.Contains("ONE")) {
    m_one = true;
  }
  if (name.Contains("Two") || name.Contains("two") || name.Contains("TWO")) {
    m_two = true;
  }

  // check lepton selection chosen correctly
  if ((m_zero && m_one) || (m_zero && m_two) || (m_one && m_two)) {
    std::cout << "CorrsAndSysts initialized for multiple analyses" << std::endl;
    exit(-1);
  }
  if (!m_zero && !m_one && !m_two) {
    std::cout << "CorrsAndSysts initialized for none of the analyses"
              << std::endl;
    exit(-1);
  }

  // set year
  if (name.Contains("7TeV")) {
    m_seven = true;
  }
  if (name.Contains("8TeV")) {
    m_eight = true;
  }
  if (name.Contains("13TeV")) {
    m_thirteen = true;
  }

  // check year is set correctly
  if (m_seven && m_eight && m_thirteen) {
    std::cout << "CorrsAndSysts initialized for 7 and 8 TeV" << std::endl;
    exit(-1);
  }
  if (!m_seven && !m_eight && !m_thirteen) {
    std::cout << "CorrsAndSysts initialized for neither 7, 8 or 13 TeV"
              << std::endl;
    exit(-1);
  }

  // write some stuff so users can be confident they did this correctly
  std::cout << "Initalize CorrsAndSysts for ";
  if (m_zero) {
    std::cout << "Zero";
  }
  if (m_one) {
    std::cout << "One";
  }
  if (m_two) {
    std::cout << "Two";
  }
  std::cout << " Lepton ";
  if (m_seven) {
    std::cout << "7";
  }
  if (m_eight) {
    std::cout << "8";
  }
  if (m_thirteen) {
    std::cout << "13";
  }
  std::cout << " TeV analysis" << std::endl;

  // initalize the corrections and systematics
  Initialize();

}  // CorrsAndSysts

CorrsAndSysts::CorrsAndSysts(int channel, int year, bool draw, bool HZZ)
    : m_debug(0),
      m_zero(false),
      m_one(false),
      m_two(false),
      m_seven(false),
      m_eight(false),
      m_thirteen(false) {
  m_draw = draw;
  m_HZZ = HZZ;

  switch (channel) {
    case 0:
      m_zero = true;
      break;
    case 1:
      m_one = true;
      break;
    case 2:
      m_two = true;
      break;
    default:
      std::cout << "CorrsAndSysts initialized for none of the analyses"
                << std::endl;
      exit(-1);
  }
  switch (year) {
    case 2011:
      m_seven = true;
      break;
    case 2012:
      m_eight = true;
      break;
    case 2015:
      m_thirteen = true;
      break;
    default:
      std::cout << "CorrsAndSysts initialized for neither 7 nor 8 nor 13 TeV"
                << std::endl;
      exit(-1);
  }

  // write some stuff so users can be confident they did this correctly
  std::cout << "Initalize CorrsAndSysts for ";
  if (m_zero) {
    std::cout << "Zero";
  }
  if (m_one) {
    std::cout << "One";
  }
  if (m_two) {
    std::cout << "Two";
  }
  std::cout << " Lepton ";
  if (m_seven) {
    std::cout << "7";
  }
  if (m_eight) {
    std::cout << "8";
  }
  if (m_thirteen) {
    std::cout << "13";
  }

  std::cout << " TeV analysis" << std::endl;

  // initalize the corrections and systematics
  Initialize();

}  // CorrsAndSysts

