CAS::SysBin CorrsAndSysts::GetSysBin(float vpt) {
  if (vpt < pTbins[0]) {
    std::cout << "CorrsAndSysts::ERROR - V pT " << vpt
              << " is smaller than lowest bin boundary " << pTbins[0]
              << std::endl;
  }
  if (vpt < pTbins[1]) {
    return CAS::Bin0;
  }
  if (vpt < pTbins[2]) {
    return CAS::Bin1;
  }
  if (vpt < pTbins[3]) {
    return CAS::Bin2;
  }
  if (vpt < pTbins[4]) {
    return CAS::Bin3;
  }
  return CAS::Bin4;

}  // GetSysBin

