float CorrsAndSysts::Get_BkgNJetCorrection(CAS::EventType type, int njet) {
  float scale = 0;
  if ((type == CAS::Z || type == CAS::W) ||  // for "inclusive" W and Z - FLS
      (type == CAS::Zl || type == CAS::Zcc || type == CAS::Zc ||
       type == CAS::Zb) ||
      (type == CAS::Wl || type == CAS::Wcc || type == CAS::Wc ||
       type == CAS::Wb)) {
    // Only applied to Sherpa v2.2 - assume is the default
    return m_PMGSherpa22VJetsWeightTool->getSherpa22VJets_NJetCorrection(njet);
  }
  return 1 + scale;
}

// if using this function
//  need to have Up or Do as the last two characters of the syst name
//  the string Bin in the name if necessary
void CorrsAndSysts::GetSystFromName(TString name, CAS::Systematic& sys,
                                    CAS::SysBin& bin, CAS::SysVar& var) {
  if (name.EndsWith("Up") || name.EndsWith("Do")) {
    var = m_varFromNames[name(name.Length() - 2, 2).Data()];
    name.Remove(name.Length() - 2);
  }
  if (name.Contains("Bin")) {
    bin = m_binFromNames[name(name.Length() - 4, 4).Data()];
    name.Remove(name.Length() - 4);
  } else {
    bin = CAS::Any;
  }
  sys = m_systFromNames[name.Data()];
}

// faster method, which does not allow to determine bin or direction
void CorrsAndSysts::GetSystFromName(const TString& name, CAS::Systematic& sys) {
  sys = m_systFromNames[name.Data()];
}

void CorrsAndSysts::WriteHistsToFile(TString fname) {
  TFile* file = new TFile(fname, "RECREATE");
  for (auto hists = m_allHists.begin(); hists != m_allHists.end(); hists++) {
    hists->second->Write();
  }
  file->Close();
  delete file;
}  // WriteHistsToFile

